INSERT INTO `listproducts` (`id`, `name`, `manu_id`, `type_id`, `price`, `image`, `image1`, `image2`, `image3`, `description`, `feature`, `created_at`, `dimensions`, `display_size`, `product_code`) VALUES
(1, 'IPhone 12 64GB', 1, 2, 20990000, 'iphone-12-do-1-1-org.jpg', 'iphone-12-do-4-org.jpg', 'iphone-12-den-1-1-org.jpg', 'iphone-12-trang-1-1-org.jpg', 'Trong những tháng cuối năm 2020, Apple đã chính thức giới thiệu đến người dùng cũng như iFan thế hệ iPhone 12 series mới với hàng loạt tính năng bứt phá, thiết kế được lột xác hoàn toàn, hiệu năng đầy mạnh mẽ và một trong số đó chính là iPhone 12 64GB.', 0, '2020-06-24 14:16:13', 'Dài 146.7 mm - Ngang 71.5 mm - Dày 7.4 mm - Nặng 164 g', 'OLED6.1\"Super Retina XDR', 'p1000'),
(2, 'IPhone 13 Pro Max 128GB', 1, 2, 33990000, 'iphone-13-pro-max-gold-1-1.jpg', 'iphone-13-pro-max-gold-2.jpg', 'iphone-13-pro-max-grey-2.jpg', 'iphone-13-pro-max-silver-2.jpg', 'IPhone 13 Pro Max 128GB - siêu phẩm được mong chờ nhất ở nửa cuối năm 2021 đến từ Apple. Máy có thiết kế không mấy đột phá khi so với người tiền nhiệm, bên trong đây vẫn là một sản phẩm có màn hình siêu đẹp, tần số quét được nâng cấp lên 120 Hz mượt mà, cảm biến camera có kích thước lớn hơn, cùng hiệu năng mạnh mẽ với sức mạnh đến từ Apple A15 Bionic, sẵn sàng cùng bạn chinh phục mọi thử thách.', 0, '2021-04-21 14:16:13', 'Dài 160.8 mm - Ngang 78.1 mm - Dày 7.65 mm - Nặng 240 g', 'OLED6.7\"Super Retina XDR', 'p1001'),
(3, 'IPhone XR 64GB', 1, 2, 13490000, 'iphone-xr-den-1-1-org.jpg', 'iphone-xr-den-4-org.jpg', 'iphone-xr-xanh-1-1-org.jpg', 'iphone-xr-xanh-2-org.jpg', 'Là chiếc điện thoại iPhone có mức giá dễ chịu, phù hợp với nhiều khách hàng hơn, iPhone Xr vẫn được ưu ái trang bị chip Apple A12 mạnh mẽ, màn hình tai thỏ cùng khả năng kháng nước kháng bụi', 1, '2021-05-20 14:22:14', 'Dài 150.9 mm - Ngang 75.7 mm - Dày 8.3 mm - Nặng 194 g', 'IPS LCD6.1\"Liquid Retina', 'p1002'),
(4, 'IPhone 12 Pro Max 128GB', 1, 2, 31990000, 'iphone-12-pro-max-xanh-duong-1-org.jpg', 'iphone-12-pro-max-xanh-duong-4-org.jpg', 'iphone-12-pro-max-xanh-duong-5-org.jpg', 'iphone-12-pro-max-bac-4-org.jpg', 'iPhone 12 Pro Max 128 GB một siêu phẩm smartphone đến từ Apple. Máy có một hiệu năng hoàn toàn mạnh mẽ đáp ứng tốt nhiều nhu cầu đến từ người dùng và mang trong mình một thiết kế đầy vuông vức, sang trọng.', 1, '2021-04-24 14:22:14', 'Dài 160.8 mm - Ngang 78.1 mm - Dày 7.4 mm - Nặng 228 g', 'OLED6.7\"Super Retina XDR', 'p1003'),
(5, 'IPhone SE 128GB (2020) ', 1, 2, 12490000, 'iphone-se-2020-do-1-1-org.jpg', 'iphone-se-2020-do-4-org.jpg', 'iphone-se-2020-do-5-1-org.jpg', 'iphone-se-2020-do-12-org.jpg', 'iPhone SE 2020 khi được cho ra mắt đã làm thỏa mãn triệu tín đồ Táo khuyết. Máy sở hữu thiết kế siêu nhỏ gọn như iPhone 8, chip A13 Bionic cho hiệu năng khủng như iPhone 11, nhưng iPhone SE 2020 lại có một mức giá tốt đến bất ngờ.', 0, '2020-10-21 14:27:12', 'Dài 138.4 mm - Ngang 67.3 mm - Dày 7.3 mm - Nặng 148 g', 'IPS LCD4.7\"', 'p1004'),
(6, 'Samsung Galaxy Z Fold3 5G 512GB', 2, 2, 44990000, 'samsung-galaxy-z-fold-3-green2-org.jpg', 'samsung-galaxy-z-fold-3-green3-org.jpg', 'samsung-galaxy-z-fold-3-green-gc-org.jpg', 'samsung-galaxy-z-fold-3-5-org.jpg', 'Galaxy Z Fold3 5G đánh dấu bước tiến mới của Samsung trong phân khúc điện thoại gập cao cấp khi được cải tiến về độ bền cùng những nâng cấp đáng giá về cấu hình hiệu năng, hứa hẹn sẽ mang đến trải nghiệm sử dụng đột phá cho người dùng.', 1, '2021-05-22 14:27:13', 'Dài 158.2 mm - Ngang 128.1 mm - Dày 6.4 mm - Nặng 271 g', 'Dynamic AMOLED 2XChính 7.6\" & Phụ 6.2\"Full HD+', 'p1005'),
(7, 'Samsung Galaxy A03s', 2, 2, 3490000, 'samsung-galaxy-a03s-black-gc-org.jpg', 'samsung-galaxy-a03s-black-2.jpg', 'samsung-galaxy-a03s-black-4.jpg', 'samsung-galaxy-a03s-white-gc-org.jpg', 'Nhằm đem đến cho người dùng thêm sự lựa chọn trong phân khúc, Samsung đã cho ra mắt thêm một chiếc điện thoại giá rẻ với tên gọi Galaxy A03s. So với người tiền nhiệm Galaxy A02s, thiết bị sẽ có một số nâng cấp mới, đây hứa hẹn sẽ là sản phẩm đáng để bạn trải nghiệm.', 0, '2019-12-28 14:32:17', 'Dài 164 mm - Ngang 7.6 mm - Dày 9.1 mm - Nặng 196 g', 'PLS LCD6.5\"HD+', 'p1006'),
(8, 'Samsung Galaxy Z Fold2 5G', 2, 2, 44000000, 'samsung-galaxy-z-fold-2-den-1-org.jpg', 'samsung-galaxy-z-fold-2-den-5-org.jpg', 'samsung-galaxy-z-fold-2-den-6-org.jpg', 'samsung-galaxy-z-fold-2-den-7-org.jpg', 'Galaxy Z Fold 2 là tên gọi chính thức của điện thoại màn hình gập cao cấp của Samsung. Với nhiều nâng cấp tiên phong về thiết kế, hiệu năng và camera, hứa hẹn đây sẽ là một siêu phẩm thành công tiếp theo đến từ “ông trùm” sản xuất điện thoại lớn nhất thế giới.', 1, '2021-05-27 14:32:17', 'Dài 159.2 mm - Ngang 128.2 mm - Dày 6.9 mm - Nặng 282 g', 'Chính: Dynamic AMOLED 2X, Phụ: Super AMOLEDChính 7.59\" & Phụ 6.23\"Full HD+', 'p1007'),
(9, 'Samsung Galaxy Z Flip3 5G 128GB ', 2, 2, 24990000, 'samsung-galaxy-z-flip-3-kem-1-org.jpg', 'samsung-galaxy-z-flip-3-kem-2-org.jpg', 'samsung-galaxy-z-flip-3-kem-5-org.jpg', 'samsung-galaxy-z-flip-3-kem-6-org.jpg', 'Trong sự kiện Galaxy Unpacked hồi 11/8, Samsung đã chính thức trình làng mẫu điện thoại màn hình gập thế hệ mới mang tên Galaxy Z Flip3 5G 128GB. Đây là một siêu phẩm với màn hình gập dạng vỏ sò cùng nhiều điểm cải tiến và thông số ấn tượng, sản phẩm chắc chắn sẽ thu hút được rất nhiều sự quan tâm của người dùng, đặc biệt là phái nữ.', 1, '2021-02-22 14:37:29', 'Dài 166 mm - Ngang 72.2 mm - Dày 6.9 mm - Nặng 183 g', 'Dynamic AMOLED 2XChính 6.7\" & Phụ 1.9\"Full HD+', 'p1008'),
(10, 'Samsung Galaxy A51 (6GB/128GB)', 2, 2, 7490000, 'samsung-galaxy-a51-bac-inox-1-org.jpg', 'samsung-galaxy-a51-xanh-ngoc-1-org.jpg', 'samsung-galaxy-a51-bac-inox-4-org.jpg', 'samsung-galaxy-a51-xanh-ngoc-5-org.jpg', 'Theo Strategy Analytics, Galaxy A51 là Smartphone Android Bán Chạy Nhất Thế Giới Quý 1/2020, máy sở hữu cụm 4 camera, bao gồm camera macro chụp cận cảnh lần đầu xuất hiện trên smartphone Samsung, màn hình tràn viền vô cực cùng mặt lưng họa tiết kim cương nổi bật.', 0, '2019-07-15 14:37:29', 'Dài 158.4 mm - Ngang 73.7 mm - Dày 7.9 mm - Nặng 172 g', 'Super AMOLED6.5\"Full HD+', 'p1009'),
(11, 'Laptop Apple MacBook Air M1 2020', 1, 1, 28990000, 'macbook-air-m1-2020-gold-02-org.jpg', 'macbook-air-m1-2020-gold-03-org.jpg', 'macbook-air-m1-2020-gold-07-org.jpg', 'macbook-air-m1-2020-silver-03-org.jpg', 'Laptop Apple MacBook Air M1 2020 thuộc dòng laptop cao cấp sang trọng có cấu hình mạnh mẽ, chinh phục được các tính năng văn phòng lẫn đồ hoạ mà bạn mong muốn, thời lượng pin dài, thiết kế mỏng nhẹ sẽ đáp ứng tốt các nhu cầu làm việc của bạn.', 0, '2020-09-23 14:42:38', 'Dài 304.1 mm - Rộng 212.4 mm - Dày 4.1 mm đến 16.1 mm - Nặng 1.29 kg', '13.3\"Retina (2560 x 1600)', 'p1010'),
(12, 'Laptop Apple MacBook Pro 16 M1 Max 2021', 1, 1, 90990000, 'apple-macbook-pro-16-m1-max-2021-1.jpg', 'apple-macbook-pro-16-m1-max-2021-2.jpg', 'apple-macbook-pro-16-m1-max-2021-bac-4.jpg', 'apple-macbook-pro-16-m1-max-2021-5-2.jpg', 'Thật ấn tượng với Apple MacBook Pro 16 M1 Max 2021 mang trên mình \"bộ áo mới\" độc đáo, cuốn hút mọi ánh nhìn cùng màn hình tai thỏ lần đầu tiên xuất hiện ở dòng Mac và ẩn bên trong là bộ cấu hình mạnh mẽ tuyệt vời đến từ con chip M1 Max tân tiến.', 1, '2021-06-06 14:42:38', 'Dài 355.7 mm - Rộng 248.1 mm - Dày 16.8 mm - Nặng 2.2 kg', '16.2 inchLiquid Retina XDR display (3456 x 2234)120Hz', 'p1011'),
(13, 'Laptop Apple MacBook Pro 14 M1 Pro 2021', 1, 1, 64990000, 'apple-macbook-pro-14-m1-pro-2021-10-core-cpu-1.jpg', 'apple-macbook-pro-14-m1-pro-2021-10-core-cpu-2.jpg', 'apple-macbook-pro-14-m1-pro-2021-10-core-cpu-4.jpg', 'macbook-pro-14-m1-pro-2021-bac-3.jpg', 'Apple MacBook Pro 14 inch M1 Pro 2021 gây ấn tượng mạnh khi mang trên mình vẻ ngoài có nhiều cải tiến mới, độc đáo và cuốn hút mọi ánh nhìn cùng hiệu năng mạnh mẽ, đỉnh cao đến từ con chip M1 Pro hiện đại, đáp ứng tối ưu nhu cầu sử dụng cho giới công nghệ, kỹ thuật cũng như cá nhà sáng tạo nội dung chuyên nghiệp.', 1, '2021-10-19 14:47:04', 'Dài 312.6 mm - Rộng 221.2 mm - Dày 15.5 mm - Nặng 1.6 kg', '14.2 inchLiquid Retina XDR display (3024 x 1964)120Hz', 'p1012'),
(14, 'Laptop Apple MacBook Pro M1 2020', 1, 1, 39990000, 'space-1-org.jpg', 'space-2-org.jpg', 'space-3-org.jpg', 'space-5-org.jpg', 'Apple Macbook Pro M1 2020 với hiệu năng cực kỳ mạnh mẽ tích hợp chip Apple M1 lần đầu xuất hiện trên dòng Mac, con laptop này hứa hẹn sẽ mang đến cho bạn một sản phẩm “Pro” chưa từng thấy.', 0, '2020-11-20 14:47:04', 'Dài 304.1 mm - Rộng 212.4 mm - Dày 15.6 mm - Nặng 1.4 kg', '13.3\"Retina (2560 x 1600)', 'p1013'),
(15, 'Samsung Notebook 9', 2, 1, 20600000, 'uploaded_c100e833bf7ccb3d581c72939c3674f9.jpg', 'uploaded_8f7b691fef8aa96adfc944ffe06391b0.jpg', 'uploaded_1e9bc050c281b6be80d7ebc728b5078e.jpg', 'uploaded_c052010832f297d711f8c7fb7e2abb36.jpg', 'Samsung Notebook 9 là sản phẩm của Samsung, một thương hiệu nổi tiếng về các thiết bị công nghệ đến từ Hàn Quốc. Thiết kế của chiếc máy tính xách tay này là một trong những ưu điểm, với trọng lượng chỉ 0,84kg với phiên bản 13\" và 1,29kg với phiên bản 15\" rất tiện lợi cho việc đem theo khi đi ra ngoài và dễ dàng sử dụng mọi lúc mọi nơi.', 0, '2021-05-11 14:54:39', 'Dài 355.7 mm - Rộng 248.1 mm - Dày 16.8 mm - Nặng 2.2 kg', '15.6\" LCD', 'p1014'),
(16, 'Samsung Galaxy Book Flex', 2, 1, 31550000, 'uploaded_20335ac8c9dc3d6c6c05498d2d7ee174.jpg', 'uploaded_0d69be263aeb242d285747dc836c64d8.jpg', 'uploaded_aa281a6faf8b9fe45d92b109ced0919f.jpg', 'uploaded_87caa0968f68b96e0b59cb51ebcf677d.jpg', 'Laptop Samsung Galaxy Book Flex là chiếc máy tính xách tay chạy hệ điều hành Windows 10 Home, tốc độ xung nhịp 3.7GHz giúp máy chạy mượt mà và đa nhiệm. Laptop được trang bị màn hình kích thước 13.3\" cùng công nghệ IPS và độ phân giải 1920 x 1080pixels đem đến hình ảnh hiện thị sắc nét và chân thực.Laptop Samsung Galaxy Book Flex có trọng lượng chỉ 1.2kg thuận tiện mang theo đi làm mỗi ngày.', 1, '2021-08-30 14:54:39', 'Dài 355.7 mm - Rộng 248.1 mm - Dày 16.8 mm - Nặng 2.2 kg', '16.2 inchLiquid Retina XDR display (3456 x 2234)120Hz', 'p1015'),
(17, 'Laptop Dell Vostro 3400 i5', 3, 1, 18890000, 'dell-vostro-3400-i5-70253900-1.jpg', 'dell-vostro-3400-i5-70253900-2.jpg', 'dell-vostro-3400-i5-70253900-4.jpg', 'dell-vostro-3400-i5-70253900-13.jpg', 'Với hiệu năng vượt trội đến từ con chip Intel Gen 11 tân tiến ẩn bên trong vẻ ngoài mang phong cách tối giản, thanh lịch, laptop Dell Vostro 3400 i5 (70253900) sẽ là một trong những gợi ý đáng để bạn tìm hiểu và chọn mua.', 0, '2019-08-12 15:00:33', 'Dài 328.7 mm - Rộng 239.5 mm - Dày 19.9 mm - Nặng 1.64 kg', '14\"Full HD (1920 x 1080)', 'p1016'),
(18, 'Laptop Dell Gaming G3 15 i7', 3, 1, 31990000, 'dell-g3-15-i7-p89f002bwh-2-org.jpg', 'dell-g3-15-i7-p89f002bwh-1-org.jpg', 'dell-g3-15-i7-p89f002bwh-3-org.jpg', 'dell-g3-15-i7-p89f002bwh-4-org.jpg', 'Laptop Dell G3 15 i7 (P89F002BWH) thuộc dòng laptop gaming với cấu hình mạnh mẽ, thiết kế trang nhã và rất sang trọng, dễ lựa chọn cho cả người đi đọc, đi làm, là 1 phiên bản tốt để lựa chọn cho cả nhu cầu làm việc, học tập và chơi game giải trí.', 1, '2021-06-19 15:00:33', 'Dài 364.46 mm - Rộng 254 mm - Dày 30.96 mm - Nặng 2.58 kg', '15.6\"Full HD (1920 x 1080)120Hz', 'p1017'),
(19, 'Apple Watch SE', 1, 5, 8990000, 'untitled-1-org.jpg', 'se-40mm-vien-nhom-day-cao-su-hong-glr-1-org.jpg', 'se-40mm-vien-nhom-day-cao-su-hong-glr-2-org.jpg', 'se-40mm-vien-nhom-day-cao-su-hong-glr-3-org.jpg', 'Apple Watch SE 40mm viền nhôm dây cao su hồng có khung viền chắc chắn, thiết kế bo tròn 4 góc giúp thao tác vuốt chạm thoải mái hơn. Mặt kính cường lực Ion-X strengthened glass với kích thước 1.57 inch, hiển thị rõ ràng. Khung viền nhôm chắc chắn cùng dây đeo cao su có độ đàn hồi cao, êm ái, tạo cảm giác thoải mái khi đeo.', 0, '2021-01-23 15:05:02', 'Dài 40 mm - Ngang 34 mm - Dày 10.7 mm - Nặng 30.49g', 'OLED1.57 inch', 'p1018'),
(20, 'Apple Watch Series 7 LTE', 1, 5, 22990000, 'apple-watch-series-7-lte-45mm-day-thep-bac-1.jpg', 'apple-watch-series-7-lte-45mm-day-thep-bac-2.jpg', 'apple-watch-series-7-lte-45mm-day-thep-vang-1.jpg', 'apple-watch-series-7-lte-45mm-day-thep-den-1.jpg', 'Apple Watch S7 LTE 45 mm sở hữu khung viền thép không gỉ cứng cáp, sang trọng cùng thiết kế bo cong mềm mại ở phần góc và mặt kính Sapphire có kích thước 1.77 inch (diện tích màn hình tăng 20% so với phiên bản cũ), bảo vệ tốt mặt kính trước những va đập. Phần viền đồng hồ được được làm mỏng nhẹ chỉ 1.7 mm, tạo cảm giác tràn viền nhiều hơn (phần viền mỏng hơn 60% so với Apple Watch S6).', 1, '2021-06-06 15:05:02', 'Dài 45 mm - Ngang 38 mm - Dày 10.7 mm - Nặng 51.5g', 'OLED1.77 inch', 'p1019'),
(21, 'Samsung Galaxy Watch 3', 2, 5, 4990000, 'samsung-galaxy-watch-3-45mm-bac-2-org.jpg', 'samsung-galaxy-watch-3-45mm-bac-3-org.jpg', 'samsung-galaxy-watch-3-45mm-bac-4-org.jpg', 'samsung-galaxy-watch-3-45mm-bac-5-org.jpg', 'Samsung Galaxy Watch 3 45mm viền thép bạc dây da với tấm nền Super AMOLED 1.4 inch và độ phân giải 360x360 pixels, đồng hồ có khả năng hiển thị xuất sắc với màu sắc rực rỡ, thông tin hiển thị đầy đủ, rõ ràng. Khung viền của đồng hồ được làm bằng thép không gỉ chắc chắn và chống ăn mòn.', 0, '2020-08-08 15:08:14', 'Dài 46.2 mm - Ngang 45 mm - Dày 11.1 mm - Nặng 48.2g', 'SUPER AMOLED1.4 inch', 'p1020'),
(22, 'Samsung Galaxy Watch 4 LTE Classic 46mm', 2, 5, 9990000, 'samsung-galaxy-watch-4-lte-classic-46mm1-org.jpg', 'samsung-galaxy-watch-4-lte-classic-46mm2-org.jpg', 'samsung-galaxy-watch-4-lte-classic-46mm3-org.jpg', 'samsung-galaxy-watch-4-lte-classic-46mm4-1-org.jpg', 'Samsung Galaxy Watch 4 LTE Classic 46mm mang nét sang trọng, hiện đại cùng với bộ khung viền thép không gỉ cứng cáp, màn hình SUPER AMOLED được bao bọc bởi lớp kính cường lực Gorrilla Glass Dx+ bền bỉ, chịu lực tốt. Dây đeo silicone êm nhẹ, độ đàn hồi cao, không thấm nước, thiết kế đục lỗ cho người dùng sự thông thoáng khi mang.', 1, '2021-07-10 15:08:14', 'Dài 45.5 mm - Ngang 45.5 mm - Dày 11 mm - Nặng 52g', 'SUPER AMOLED1.36 inch', 'p1021'),
(23, 'Mi Watch', 5, 5, 2390000, 'xanh-1-org.jpg', 'xanh-2-org.jpg', 'xanh-3-org.jpg', 'mi-watch-1-org.jpg', 'Đồng hồ thông minh Mi Watch này mang phong cách trẻ trung, cá tính và đậm chất thể thao. Đồng hồ được trang bị công nghệ màn hình AMOLED với kích thước 1.39 inch cùng độ phân giải 454 x 454 pixels và độ sáng lên đến 450 nits giúp người dùng có thể quan sát thông tin rõ nét, chất lượng. Bên cạnh đó, đồng hồ còn được trang bị mặt kính cường lực Gorilla Glass 3 hạn chế trầy xước và tăng độ bền cho thiết bị. ', 0, '2020-08-12 15:12:03', 'Dài 53.3 mm - Ngang 45.9 mm - Dày 11.8 mm - Nặng 32g', 'AMOLED1.39 inch', 'p1022'),
(24, 'Mi Band 6', 5, 5, 949000, 'mi-band-6-1-2-org.jpg', 'mi-band-6-2-1-org.jpg', 'mi-band-6-3-1-org.jpg', 'mi-band-6-8-org.jpg', 'Vòng đeo tay thông minh Mi Band 6 là phiên bản đáng mong đợi của nhà Xiaomi với thiết kế màn hình tràn viền cho bạn góc nhìn tốt hơn. Mặt kính cường lực chống trầy xước tốt cùng dây đeo cao su với thiết kế ôm trọn cổ tay, không thấm nước khi đeo, mang lại cho bạn cảm giác dễ chịu cả ngày dài.', 0, '2019-03-15 15:12:03', 'Dài 47.4 mm - Ngang 18.6 mm - Dày 12.7 mm - Nặng 12.8g', 'AMOLED1.56 inch', 'p1023'),
(25, 'Tai nghe Bluetooth True Wireless Sony WF-C500', 4, 4, 2290000, 'bluetooth-true-wireless-sony-wf-c500-trang-2.jpg', 'bluetooth-true-wireless-sony-wf-c500-trang-3.jpg', 'bluetooth-true-wireless-sony-wf-c500-trang-4.jpg', 'bluetooth-true-wireless-sony-wf-c500-trang-7.jpg', 'Tai nghe Bluetooth True Wireless Sony WF-C500 được thiết kế nhỏ gọn, bo tròn các góc cạnh để vừa khớp với đôi tai của bạn cho cảm giác dễ chịu khi đeo. Bạn có thể tùy thích lựa chọn chiếc tai nghe phù hợp cho phong cách, sở thích của bản thân với các màu trắng, đen, cam, xanh ngọc.', 0, '2020-10-21 14:27:12', 'Bluetooth 5.0', '360 Reality AudioDSEE', 'p1024'),
(26, 'Tai nghe True Wireless Sony WF-1000XM4', 4, 4, 6490000, 'bluetooth-true-wireless-sony-wf-1000xm4-bme5-org.jpg', 'bluetooth-true-wireless-sony-wf-1000xm4-bme1-org.jpg', 'bluetooth-true-wireless-sony-wf-1000xm4-bme3-org.jpg', 'bluetooth-true-wireless-sony-wf-1000xm4-bme2-org.jpg', 'Tai nghe Bluetooth True Wireless Sony WF-1000XM4 được phủ gam màu đen nhám sang trọng, thiết kế nhỏ gọn dễ dàng bỏ túi mang đi khắp nơi. Hộp sạc được thiết kế có nam châm đóng mở nhẹ nhàng. ', 1, '2021-11-01 15:16:27', 'Chống ồn HD QN1', 'Type-C', 'p1025'),
(27, 'Tai nghe Bluetooth True Wireless Galaxy Buds Pro Bạc', 2, 4, 4990000, 'tai-nghe-bluetooth-true-wireless-galaxy-buds-pro-bac-1-org.jpg', 'tai-nghe-bluetooth-true-wireless-galaxy-buds-pro-bac-3-org.jpg', 'tai-nghe-bluetooth-true-wireless-galaxy-buds-pro-bac-6-org.jpg', 'tai-nghe-bluetooth-true-wireless-galaxy-buds-pro-bac-7-org.jpg', 'Tai nghe Bluetooth True Wireless Samsung Buds Pro sở hữu vẻ ngoài đẹp mắt thời thượng với hai màu đen và trắng. Thiết kế mới trên hình dạng tai nghe cổ điển, có khả năng làm giảm bớt sự khó chịu khi sử dụng tai nghe trong nhiều giờ và tai nghe cũng nằm chắc chắn phía trong tai khi bạn tập luyện hay vận động.', 1, '2021-01-17 15:22:35', 'Type-C', 'Active Noise CancellationDolby Head Tracking', 'p1026'),
(28, 'Tai nghe Bluetooth Samsung Level U Pro BN920C ', 2, 4, 1450000, 'tai-nghe-bluetooth-samsung-level-u-pro-bn920c-vang-1-org.jpg', 'tai-nghe-bluetooth-samsung-level-u-pro-bn920c-vang-2-org.jpg', 'tai-nghe-bluetooth-samsung-level-u-pro-bn920c-vang-5-org.jpg', 'tai-nghe-bluetooth-samsung-level-u-pro-bn920c-vang-7-org.jpg', 'Tai nghe Samsung Level U Pro BN920C được thiết kế sang trọng\r\nToàn thân tai nghe được gia công bằng vỏ nhựa và cao su, phần cao su sẽ giúp tai nghe tiếp xúc với da trơn hơn, thoải mái hơn.', 1, '2021-05-23 15:22:35', 'Dùng 9 giờ - Sạc 3 giờ', 'Giảm tiếng ồn NR/EC', 'p1027'),
(29, 'Camera IP Mi Home Xiaomi BHR4885GL', 5, 3, 854000, 'camera-ip-mi-home-360-do-1080p-xiaomi-bhr4885gl-2-2-org.jpg', 'camera-ip-mi-home-360-do-1080p-xiaomi-bhr4885gl-3-1-org.jpg', 'camera-ip-mi-home-360-do-1080p-xiaomi-bhr4885gl-4-1-org.jpg', 'camera-ip-mi-home-360-do-1080p-xiaomi-bhr4885gl-6-1-org.jpg', 'Camera IP Mi Home Xiaomi BHR4885GL cho phép sử dụng linh hoạt ổ nhiều vị trí với khả năng chống rung tốt, đảm bảo ổn định cho chất lượng dữ liệu quan sát.', 1, '2020-05-29 15:57:20', 'Dài 11 cm - Rộng 7 cm - Cao 11 cm', 'Xoay dọc 108 độXoay ngang 360 độ', 'p1028'),
(30, 'Camera 1080P Xiaomi Mi Dash Cam 1S', 5, 3, 1293000, 'camera-hanh-trinh-1080p-xiaomi-mi-dash-cam-1s-den-1-org.jpg', 'camera-hanh-trinh-1080p-xiaomi-mi-dash-cam-1s-den-3-org.jpg', 'camera-hanh-trinh-1080p-xiaomi-mi-dash-cam-1s-den-4-org.jpg', 'camera-hanh-trinh-1080p-xiaomi-mi-dash-cam-1s-den-5-org.jpg', 'Camera Xiaomi Mi Dash Cam 1S Đen tạo sự tinh tế trong không gian nội thất xe, đảm bảo nguồn dữ liệu tin cậy cho những trường hợp sử dụng khẩn cấp.', 0, '2020-02-15 15:57:20', 'Dài 11 cm - Rộng 7 cm - Cao 11 cm', 'LCD 2.7 inch', 'p1029'),
(31, 'Camera IP 1080P Xiaomi Mi Home Magnetic Mount', 5, 3, 610000, 'ip-1080p-xiaomi-mi-home-magnetic-mount-qdj4065gl-1-org.jpg', 'ip-1080p-xiaomi-mi-home-magnetic-mount-qdj4065gl-3-org.jpg', 'ip-1080p-xiaomi-mi-home-magnetic-mount-qdj4065gl-5-org.jpg', 'ip-1080p-xiaomi-mi-home-magnetic-mount-qdj4065gl-8-org.jpg', 'Bạn có thể chọn lựa Camera IP Xiaomi Mi Home Magnetic Mount QDJ4065GL cho không gian hộ gia đình, hoặc dùng cho các khu vực công cộng, hệ thống an ninh văn phòng...', 0, '2021-02-20 16:04:21', 'Full HD 1080p', 'Góc rộng 170°', 'p1030'),
(32, 'Camera 2K Xiaomi BHR4193GL', 5, 3, 1431000, 'ip-mi-home-360-do-2k-pro-xiaomi-bhr4193gl-2-2-org.jpg', 'ip-mi-home-360-do-2k-pro-xiaomi-bhr4193gl-3-2-org.jpg', 'ip-mi-home-360-do-2k-pro-xiaomi-bhr4193gl-5-1-org.jpg', 'ip-mi-home-360-do-2k-pro-xiaomi-bhr4193gl-8-1-org.jpg', 'Camera IP Mi Home 360 Độ 2K Pro Xiaomi BHR4193GL thiết kế nhỏ gọn, kiểu dáng hiện đại, tinh tế, sử dụng để bàn, kệ tủ hay lắp gắn trần dễ dàng, quan sát hiệu quả, chất lượng hình ảnh tốt, chống rung hiệu quả.', 1, '2020-03-28 16:04:21', 'Dài 12.5 cm - Rộng 7.7 cm - Cao 12.5 cm', '2304 × 1296 2K', 'p1031'),
(33, 'Sony CyberShot DSC WX350', 4, 3, 5990000, 'sony-cybershot-dsc-wx350-may-anh-du-lich(1).jpg', 'sony-cybershot-dsc-wx350-may-anh-du-lich1.jpg', 'sony-cybershot-dsc-wx350-may-anh-du-lich4.jpg', 'sony-cybershot-dsc-wx350-may-anh-du-lich5.jpg', 'WX350 của Sony tích hợp zoom quang học 20x vào một thân máy nhỏ gọn đáng ngạc nhiên - Sony tuyên bố đây là chiếc máy ảnh nhỏ nhất và nhẹ nhất thế giới. Máy ảnh Sony CyberShot DSC-WX350 này có cảm biến 18 Megapixel, quay phim 1080/50p/60p, tích hợp Wifi với NFC và chụp ảnh liên tục 10 khung hình/giây, làm cho nó có khả năng khá tốt so với mức giá. ', 1, '2020-10-24 16:15:19', 'Trọng lượng 137g', 'Zoom quang học 20x (25-500mm) Zoom số 306x', 'p1032'),
(34, 'Sony CyberShot DSC WX350', 4, 3, 5990000, 'sony-cybershot-dsc-wx350-may-anh-du-lich(1).jpg', 'sony-cybershot-dsc-wx350-may-anh-du-lich1.jpg', 'sony-cybershot-dsc-wx350-may-anh-du-lich4.jpg', 'sony-cybershot-dsc-wx350-may-anh-du-lich5.jpg', 'WX350 của Sony tích hợp zoom quang học 20x vào một thân máy nhỏ gọn đáng ngạc nhiên - Sony tuyên bố đây là chiếc máy ảnh nhỏ nhất và nhẹ nhất thế giới. Máy ảnh Sony CyberShot DSC-WX350 này có cảm biến 18 Megapixel, quay phim 1080/50p/60p, tích hợp Wifi với NFC và chụp ảnh liên tục 10 khung hình/giây, làm cho nó có khả năng khá tốt so với mức giá. ', 1, '2020-10-24 16:15:19', 'Trọng lượng 137g', 'Zoom quang học 20x (25-500mm) Zoom số 306x', 'p1033'),
(35, 'Sony Alpha A6400', 4, 3, 23490000, 'may-anh-sony-alpha-ilce6400l-a6400-kit-1650mm-f3556-oss-den1.jpg', 'may-anh-sony-alpha-ilce6400l-a6400-kit-1650mm-f3556-oss-den4.jpg', 'may-anh-sony-alpha-ilce6400l-a6400-kit-1650mm-f3556-oss-den7.jpg', 'may-anh-sony-alpha-ilce6400l-a6400-kit-1650mm-f3556-oss-den5.jpg', 'Máy ảnh Sony Alpha ILCE-6400L/ A6400 Kit 16-50mm F3.5-5.6 OSS/ Đen là mẫu máy ảnh thứ tư trong loạt máy ảnh không gương lật cảm biến APS-C của Sony, cùng với A6000, A6300 và A6500. Máy ảnh có cùng cảm biến 24,2MP, cùng với bộ xử lý hình ảnh BIONZ X mới nhất, cho chất lượng hình ảnh tuyệt vời. Máy cũng có thể quay video UHD 4K ở tốc độ 30 khung hình/giây.', 1, '2021-04-19 16:15:19', 'Trọng lượng 403g', 'Màn hình cảm ứng 3.0 inch lật 180 độ', 'p1034');
